if (!document.cookie.includes('alertShown=true')) { alert("本站已弃用，建议使用新网站 https://blog.daoxi365.top/"); document.cookie = "alertShown=true; path=/; max-age=" + (60 * 60 * 24 * 365); }
function generateRandomColor(){var r=Math.floor(Math.random()*(255-200+1))+200;var g=Math.floor(Math.random()*(255-200+1))+200;var b=Math.floor(Math.random()*(255-200+1))+200;var colorHex='#'+r.toString(16)+g.toString(16)+b.toString(16);return colorHex}function setRandomGradientBackground(){var body=document.querySelector('body');var gradientColor1=generateRandomColor();var gradientColor2=generateRandomColor();var gradientColor3=generateRandomColor();var angle=Math.floor(Math.random()*361);body.style.background=`linear-gradient(${angle}deg,${gradientColor1},${gradientColor2},${gradientColor3})`}setRandomGradientBackground();
